import {
  Button,
  Card,
  Col,
  Drawer,
  Image,
  Popconfirm,
  Row,
  Skeleton,
  Space,
  Typography,
} from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import { imsAxios } from "../../../axiosInterceptor";
import { DeleteOutlined } from "@ant-design/icons";

export default function ComponentImages({ showImages, setShowImages }) {
  const [skeletonLoading, setSkeletonLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [images, setImages] = useState([]);
  const [ellipsis, setEllipsis] = useState(true);

  const getImages = async () => {
    setSkeletonLoading(true);
    const { data } = await imsAxios.post("/component/fetchImageComponent", {
      component: showImages.partNumber,
    });
    if (data.code == 200) {
      setImages(data.data);
    } else {
      setImages([]);
      toast.error(data.message.msg);
    }
    setSkeletonLoading(false);
  };
  const deleteImage = async (image) => {
    setDeleteLoading(image.image_id);
    const response = await imsAxios.post("/component/ComponentDelete", {
      component: showImages.partNumber,
      image: image.image_id,
    });
    const { data } = response;
    if (data) {
      if (data.code === 200) {
        toast.success(data.message);
        let arr = images;
        arr = images.filter((row) => row.image_id !== image.image_id);
        setImages(arr);
      }
    }
  };
  useEffect(() => {
    if (showImages) {
      getImages();
    }
  }, [showImages]);
  return (
    <Drawer
      title={`${showImages?.partCode ?? ""}`}
      placement="right"
      width="40vw"
      onClose={() => setShowImages(null)}
      open={showImages}
    >
      <Skeleton active loading={skeletonLoading} />
      <Skeleton active loading={skeletonLoading} />
      <Skeleton active loading={skeletonLoading} />
      <Row gutter={[8, 8]}>
        {!skeletonLoading && (
          <Image.PreviewGroup>
            {images.map((image) => (
              // <Col span={24}>
              // <Row style={{ margin: "20px 0px" }} justify="center">
              <Col span={12}>
                <Card
                  style={{ position: "relative", height: 300 }}
                  bodyStyle={{ padding: 5, height: 300 }}
                >
                  {/* <Space direction="vertical" size="middle"> */}
                  <div style={{ maxHeight: 200, backgroundSize: "cover" }}>
                    <Image
                      height={300}
                      width="100%"
                      // style={{ maxHeight: 300 }}
                      src={image.image_url}
                      fallback="data:image/png;base64,"
                    />
                  </div>
                  {/* <Space align="center"> */}
                  <Popconfirm
                    title="Are you sure to delete this image"
                    okText="Yes"
                    cancelText="No"
                    loading={image.image_id === deleteLoading}
                    onConfirm={() => deleteImage(image)}
                  >
                    <Button
                      style={{ position: "absolute", right: 10, top: 10 }}
                      type="primary"
                      shape="circle"
                      icon={<DeleteOutlined />}
                    />
                  </Popconfirm>
                </Card>
                {/* </Space> */}
                <Space style={{ paddingLeft: 10 }} direction="vertical">
                  <Typography.Text>{image.uploaded_by}</Typography.Text>
                  <Typography.Text>{image.uploaded_date}</Typography.Text>
                </Space>
              </Col>
              // {/* </Row> */}
            ))}
          </Image.PreviewGroup>
        )}
      </Row>
    </Drawer>
  );
}
